export default class Replay {
  constructor(main) {
    this.main = main;
    this.util = main.util;
    this.STATE_STOPPED = 0;
    this.STATE_PLAY = 1;

    this.currentStep = 0;
    this.defaultDelay = 500;

    this.currentState = this.STATE_STOPPED;
  }

  play() {
    if (this.main.operatingStateIsReplaying()) {
      this.util.debug('replay initialization cancelled due to replay already running');
      return;
    }
    this.main.setOperatingStateReplay();
    const playStep = () => {
      if (this.currentState !== this.STATE_PLAY) {
        return;
      }

      const storedElement = this.main.storage.data[this.currentState];
      this.util.debug(`replaying! Attempting to execute a ${storedElement.event_type} on ${storedElement.id}`);
      const element = document.getElementById(storedElement.id);
      const event = new Event(storedElement.event_type);
      element.dispatchEvent(event);

      if (storedElement.value !== undefined) {
        element.value = storedElement.value;
      }

      this.currentStep = this.currentStep + 1;
      this.util.debug(`current_step is now ${this.current_step}`);

      if (this.main.config.events[storedElement.event_type].delay !== undefined) {
        triggerNextStep(this.main.config.events[storedElement.event_type].delay);
      } else {
        triggerNextStep();
      }
    };
    const triggerNextStep = (delay = this.defaultDelay) => {
      this.util.debug('attempting to trigger next step');
      if (this.currentStep >= this.main.storage.data.length) {
        this.main.setOperatingStatePassive();
        return;
      }
      this.util.debug(`storage.length (${this.main.storage.data.length}) >= current_step (${this.current_step})`);
      window.setTimeout(playStep, delay);
    };
    this.util.debug('setting current state to play, and running next step!');
    this.currentState = this.STATE_PLAY;
    triggerNextStep();
  }

  pause() {
    this.util.debug('pausing...');
    this.main.setOperatingStatePassive();
    this.currentState = this.STATE_STOPPED;
  }

  reset() {
    this.uitl.debug('stopping playback and resetting counter!');
    this.main.setOperatingStatePassive();
    this.currentState = this.STATE_STOPPED;
    this.currentState = 0;
  }
}

export default class ServerStorage {
  constructor(main) {
    this.main = main;
    this.util = this.main.util;
    this.conf = this.main.config.server;
    this.loadFromServer();
  }

  readyToLoad() {
    if (!this.conf.url) {
      this.debug('Cannot make call to server without url!');
      return false;
    }
    if (!this.main.operatingStateIsPassive()) {
      this.debug('Cannot make a call to server while state is not passive!');
      return false;
    }
    return !this.conf.isLoading;
  }

  loadFromServer() {
    if (!this.readyToLoad()) {
      return;
    }
    this.conf.isLoading = true;

    let urlstring = `${this.conf.url}?session_id=${this.conf.sessionId}`;
    if (!this.conf.userId) {
      urlstring = `${urlstring}&user_id=${this.conf.userId}`;
    }
    this.util.debug(`Attempting to load from url: ${urlstring}`);
    this.util.debug(`conf.user_id: ${this.conf.user_id}, conf.session_id: ${this.conf.session_id}, conf.url: ${this.conf.url}`);
    this.util.debug('conf:');
    this.util.debugLiteral(this.conf);
    const headers = new Headers();
    headers.append('Content-Type', 'application/json');
    const config = {
      method: 'GET',
      headers,
    };
    fetch(new Request(urlstring), config).then((response) => {
      this.util.debug(response);
      if (response.status === 200) {
        this.main.storage.updateStorage(response.data);
      }
      this.isLoading = false;
    }).catch((error) => {
      this.util.debug(error);
    });
  }

  pushToServer() {
    if (!this.readyToLoad()) {
      return;
    }
    this.conf.isLoading = true;
    const headers = new Headers();
    headers.append('Content-Type', 'application/json');
    headers.append('Origin', `${this.conf.url}`);
    const config = {
      method: 'POST',
      headers,
    };
    const data = {
      user_id: this.conf.userId,
      session_id: this.conf.session_id,
      data: this.main.storage.data,
    };
    fetch(new Request(this.conf.url), config).then((response) => {
      this.isLoading = false;
    }).catch((error) => {

    });
  }
}

const DOMREPLAY_STATE_STORAGE_KEY = 'DOM_REPLAY_STATE';

const DOMREPLAY_STATE_REPLAY = 'DOMREPLAY_STATE_REPLAY';
const DOMREPLAY_STATE_RECORD = 'DOMREPLAY_STATE_RECORD';
const DOMREPLAY_STATE_READY = 'DOMREPLAY_STATE_READY';
const DOMREPLAY_STATE_INIT = 'DOMREPLAY_STATE_INIT';


const getState = () => {
	return window.localStorage.getItem(DOMREPLAY_STATE_STORAGE_KEY);
};

const setState = (state) => {
	window.localStorage.setItem(DOMREPLAY_STATE_STORAGE_KEY, state);
};

/**
 * True if state is Replay
 * @return {Boolean} - True if state is Replay
 */
export const stateIsReplay = () => {
	return getState() === DOMREPLAY_STATE_READY;
};

/**
 * True if state is Record
 * @return {Boolean} - True if state is Record
 */
export const stateIsRecord = () => {
	return getState() === DOMREPLAY_STATE_RECORD;
}

/**
 * True if state is Ready
 * @return {Boolean} - True if state is Ready
 */
export const stateIsReady = () => {
	return getState() === DOMREPLAY_STATE_READY;
};

/**
 * True if state is Init
 * @return {Boolean} - True if state is Init.
 */
export const stateIsInit = () => {
	return getState() === DOMREPLAY_STATE_REPLAY;
}

export default class Storage {
  constructor(main) {
    this.main = main;
    this.util = main.util;
    this.data = [];
    this.isInitialized = true;
    this.util.debug('Initializing Storage');
  }

  reset() {
    this.data = [];
  }

  okToStoreEvent(element) {
    if (!this.isInitialized) {
      this.util.error('attempting to add element before storage was initialized!');
      return false;
    }
    if (!this.main.operatingStateIsRecording()) {
      this.util.debug('cancelling storage due to current operating state not set to record');
      return false;
    }
    if (element.hasAttribute('DomReplayIgnore')) {
      this.util.error('Cannot track ignored element');
      return false;
    }
    if (!element || !element.id) {
      this.util.error('Cannot add element to storage without valid id!');
      return false;
    }
    return true;
  }

  addEvent(element, event, addValue = false) {
    this.util.debug('adding event to storage');
    if (!this.okToStoreEvent(element)) {
      return;
    }
    const object = {
      eventType: event,
      id: element.id,
    };
    if (addValue) {
      object.value = element.value;
    }
    this.data.push(object);
  }

  updateStorage(newData) {
    this.util.debug('updating storage.data.');
    this.util.debug('new data:');
    this.util.debugLiteral(newData);
    this.data = newData;
  }
}

export default class Util {
