const recursiveTracker = (element, trail) => {
	if (!trail.child) {
		return element;
	} else {
		return recursiveTracker(element.children.item(trail.childIndex), trail.child);
	}
	return null;
}

export const tracker = (trail) => {
	return new Promise((resolve, reject) => {
		let element = null;
		if (trail.id && !trail.child) {
			element = document.getElementById(trail.id);
		} else if (trail.id && trail.child) {
			element = recursiveTracker(document.getElementById(trail.id), trail);
		} else if (trail.tag === 'body' && trail.child) {
			element = recursiveTracker(document.getElementsByTagName(trail.tag)[0], trail);
		}
		if (!element) {
			reject(null);
		} else {
			resolve(element);
		}
	});
}

const getSiblingIndex = (element, index) => {
	if (element.previousSibling == null) {
		return index;
	}
	return getSiblingIndex(element.previousSibling, ++index);
}


export const trail = (element, child, childIndex) => {
	if (element.id) {
		return {
			id: element.id,
			child,
			tag: null,
			childIndex
		}
	} else if (element.tagName.toLowerCase() === 'body') {
		return {
			id: null,
			child,
			tag: element.tagName.toLowerCase(),
			childIndex
		}
	}
	return trail(element.parentNode, {
		id: null,
		child,
		tag: null,
		childIndex
	}, getSiblingIndex(element, 0));
}

import Storage from './storage';
