export default class Replay {
	static storageKey;


	constructor() {
		this.storageKey = 'DOMREPLAY_REPLAY_EVENTS';
	}

	load(eventObject) {
		const replayObject = {
			nextEvent: 0,
			eventObject
		};
		this._replayObject = replayObject;
	}

	clear() {
		window.localStorage.removeItem(this.storageKey);
	}

	set _replayObject(replayObject) {
		window.localStorage.setItem(this.storageKey, JSON.stringify(replayObject));
	}

	get _replayObject() {
		return JSON.parse(window.localStorage.getItem(this.storageKey));
	}

	set _nextEventIndex(index) {
		let replayObject = this._replayObject;
		replayObject.nextEvent = index;
		this._replayObject = replayObject;
	}

	get _totalEventCount() {
		let replayObject = this._replayObject;
		return replayObject.eventObject.count;
	}

	get _nextEventIndex() {
		let replayObject = this._replayObject;
		return replayObject.nextEvent;
	}

	get _nextEvent() {
		let replayObject = this._replayObject;
		let nextEventIndex = this._nextEventIndex;
		this._nextEventIndex = nextEventIndex + 1;
		return replayObject.eventObject.eventList[nextEventIndex];
	}

	reset() {
		this._nextEventIndex = 0;
	}

	getNextStep() {
		// const instance = this;
		return new Promise(resolve => {
			if (this._nextEventIndex >= this._totalEventCount) {
				resolve(null);
			}
			else {
				resolve(this._nextEvent);
			}
		});
	}


	executeEvent(element, eventObject) {
		return new Promise((resolve) => {
			setTimeout(() => {
				//Need to handle other event types aswell.
				element.click();
				resolve(false);
			}, 1000);
		});
	}

	readyStateCheck() {
		return new Promise(resolve => {
			const time = setInterval(() => {
				if (document.readyState !== 'complete') {
					Logger.debug('Waiting for dom to complete loading after navigation');
					return;
				}
				clearInterval(time);
				resolve();
			}, 500);
		});
	}

	async playStep() {
		await this.readyStateCheck();
		const nextStep = await this.getNextStep();
		if (!nextStep) {
			return new Promise(resolve => resolve(true));
		}
		// We need a way to wait for the element to appear if the dom is loading
		// something from apis.
		const element = await tracker(nextStep.trail);
		if (!nextStep && !element) {
			return new Promise(resolve => resolve(true));
		}

		const result = await this.executeEvent(element, nextStep);
		return new Promise(resolve => resolve(result));
	}

	play() {
		Logger.debug('replay: start replaying events!');
		if (!stateIsReady()) {
			Logger.debug('state is not ready');
		}

		if (!this._replayObject) {
			Logger.warn('Please load event see Replay.load');
			return;
		}
		const instance = this;
		return setStateReplay()
			.then(async () => {
				while(!await this.playStep());
				return {
					total: this._totalEventCount,
					replayed: this._nextEventIndex
				};
			}).then(({total, replayed}) => {
				if (total == replayed) {
					setStateReady(true);
				}
				return {
					total,
					replayed
				};
			});
	}
}

export default class ServerStorage {
  constructor(main) {
    this.main = main;
    this.util = this.main.util;
    this.conf = this.main.config.server;
    this.loadFromServer();
  }

  readyToLoad() {
    if (!this.conf.url) {
      this.debug('Cannot make call to server without url!');
      return false;
    }
    if (!this.main.operatingStateIsPassive()) {
      this.debug('Cannot make a call to server while state is not passive!');
      return false;
    }
    return !this.conf.isLoading;
  }

  loadFromServer() {
    if (!this.readyToLoad()) {
      return;
    }
    this.conf.isLoading = true;

    let urlstring = `${this.conf.url}?session_id=${this.conf.sessionId}`;
    if (!this.conf.userId) {
      urlstring = `${urlstring}&user_id=${this.conf.userId}`;
    }
    this.util.debug(`Attempting to load from url: ${urlstring}`);
    this.util.debug(`conf.user_id: ${this.conf.user_id}, conf.session_id: ${this.conf.session_id}, conf.url: ${this.conf.url}`);
    this.util.debug('conf:');
    this.util.debugLiteral(this.conf);
    const headers = new Headers();
    headers.append('Content-Type', 'application/json');
    const config = {
      method: 'GET',
      headers,
    };
    fetch(new Request(urlstring), config).then((response) => {
      this.util.debug(response);
      if (response.status === 200) {
        this.main.storage.updateStorage(response.data);
      }
      this.isLoading = false;
    }).catch((error) => {
      this.util.debug(error);
    });
  }

  pushToServer() {
    if (!this.readyToLoad()) {
      return;
    }
    this.conf.isLoading = true;
    const headers = new Headers();
    headers.append('Content-Type', 'application/json');
    headers.append('Origin', `${this.conf.url}`);
    const config = {
      method: 'POST',
      headers,
    };
    const data = {
      user_id: this.conf.userId,
      session_id: this.conf.session_id,
      data: this.main.storage.data,
    };
    fetch(new Request(this.conf.url), config).then((response) => {
      this.isLoading = false;
    }).catch((error) => {

    });
  }
}

/**
 * Store the Domreplay state in local storage and provides functions to
 * change state safely and check state.
 */
import { createStateError } from './error';
import { dispatchStateChangeEvent } from './dispatcher';


const DOMREPLAY_STATE_STORAGE_KEY = 'DOMREPLAY_STATE';
export const DOMREPLAY_STATE_REPLAY = 'DOMREPLAY_STATE_REPLAY';
export const DOMREPLAY_STATE_RECORD = 'DOMREPLAY_STATE_RECORD';
export const DOMREPLAY_STATE_READY = 'DOMREPLAY_STATE_READY';


/**
 * Gets the state from localstorage
 * @return {String} - current state
 */
const getState = () => {
	return window.localStorage.getItem(DOMREPLAY_STATE_STORAGE_KEY);
};

/**
 * Sets state
 * @param  {String} state - State to set
 */
const setState = (state) => {
	window.localStorage.setItem(DOMREPLAY_STATE_STORAGE_KEY, state);
};

/**
 * Clears the state storage
 */
export const clearStateStorage = () => {
	window.localstorage.removeItem(DOMREPLAY_STATE);
}

/**
 * returns true if state is empy
 * @return {Boolean} - true if state is empty otherwise false.
 */
const stateIsEmpty = () => {
	return !getState();
}

/**
 * True if state is Replay
 * @return {Boolean} - True if state is Replay
 */
export const stateIsReplay = () => {
	return getState() === DOMREPLAY_STATE_REPLAY;
};

/**
 * True if state is Record
 * @return {Boolean} - True if state is Record
 */
export const stateIsRecord = () => {
	return getState() === DOMREPLAY_STATE_RECORD;
}

/**
 * True if state is Ready
 * @return {Boolean} - True if state is Ready
 */
export const stateIsReady = () => {
	return getState() === DOMREPLAY_STATE_READY;
};

/**
 * Sets the state to ready.
 * Resolves when state has changed
 * It will reject whenever the current state is not ready.
 * @param  {Boolean} force	- no safe
 */
export const setStateReady = (force=false) => {
	return new Promise((resolve, reject) => {
		if (force || stateIsEmpty()) {
			setState(DOMREPLAY_STATE_READY)
			dispatchStateChangeEvent(getState());
			resolve(getState());
		}
		else if (stateIsReady()) {
			resolve(DOMREPLAY_STATE_READY);
		} else {
			reject(createStateError(`Cannot change state from "${getState()}" to "${DOMREPLAY_STATE_READY}"`));
		}
	});
};

/**
 * Set state safely to record.
 * Resolves when the state has changed.
 * It will reject whenever the current state is not ready.
 * @param  {Boolean} force	- no safe
 * @return {Promise}        - Returns a Promise.
 */
export const setStateRecord = (force=false) => {
	return new Promise((resolve, reject) => {
		if (force || stateIsReady()) {
			setState(DOMREPLAY_STATE_RECORD);
			dispatchStateChangeEvent(getState());
			resolve(getState());
		} else if (stateIsReplay()) {
			resolve(DOMREPLAY_STATE_RECORD);
		}else {
			reject(createStateError(`Cannot change state from "${getState()}" to "${DOMREPLAY_STATE_RECORD}"`));
		}
	});
};

/**
 * Set state safely to replay.
 * Resolves when the state has changed.
 * It will reject whenever the current state is not ready.
 * @param  {Boolean} force - no safe
 * @return {Promise}        - Returns a Promise.
 */
export const setStateReplay = (force=false) => {
	return new Promise((resolve, reject) => {
		if (force || stateIsReady()) {
			setState(DOMREPLAY_STATE_REPLAY);
			dispatchStateChangeEvent(getState());
			resolve(getState());
		} else if (stateIsReplay()) {
			resolve(DOMREPLAY_STATE_REPLAY);
		} else {
			reject(createStateError(`Cannot change state from "${getState()}" to "${DOMREPLAY_STATE_REPLAY}"`))
		}
	});
};

import Logger from './logger';
import { stateIsRecord } from './state';
import { createStorageError } from './error';
import { dispatchStorageUpdateEvent } from './dispatcher';
import { trail, tracker } from './domhound';


class Storage {

	static storageKey;
	static instance;

	constructor() {
		if (!this.instance) {
			this.instance = this;
			this.storageKey = 'DOMREPLAY_EVENT_STORAGE';
		}
		return this.instance;
	}

	clear() {
		window.localStorage.removeItem(this.storageKey);
		Logger.debug('Event storage cleared!');
	}


	_appendEvent(object) {
		let eventObject = JSON.parse(window.localStorage.getItem(this.storageKey));
		let eventList = [];
		if (!!eventObject) {
			eventList = eventObject.eventList;
		}
		else {
			eventObject = {};
			eventObject.count = 0;
			eventList = []
		}
		eventList.push(object);
		eventObject.eventList = eventList;
		eventObject.count++;
		Logger.debug(`Count: ${eventObject.count}, Event: type(${object.type}) id(${object.id}) value(${object.value})`);
		window.localStorage.setItem(this.storageKey, JSON.stringify(eventObject));
	}

	addEvent(element, type, value = null, extra = {}) {
		return new Promise((resolve, reject) => {
			if (!stateIsRecord()) {
				Logger.error('Tried to add event to localstorage, but domreplay is not in record state');
				reject(createStorageError('Tried to add event to localstorage, but domreplay is not in record state'));
			}
			let object = {
				type,
				location: window.location.href,
				trail: trail(element, null, null)
			}
			if (value) {
				object.value = value;
			}
			console.log(object);
			tracker(object.trail)
				.then(element => {
					console.log(element);
				});
			this._appendEvent(object);
			resolve(object);
		});
	}

	get eventList() {
		return JSON.parse(window.localStorage.getItem(this.storageKey));
	}
}

export default new Storage();

import {
	dispatcher,
	domreplayIgnoreClassName,
	state
} from '../';


const button = (text='', classes='', id=undefined) => {
  const button = document.createElement('button');
  button.innerHTML = text;
  button.id = id;
  button.className = `${domreplayIgnoreClassName} dom-hud-btn ${classes}`;
  return button;
}

const div = (classes='', id=undefined) => {
  const div = document.createElement('div');
  div.className = classes;
  div.id = id;
  return div;
}

export default class Hud {
  constructor(domreplay, config={}) {
  	this.config = config;
    this.domreplay = domreplay;
    this.startButton = button('Start');
    this.stopButton = button('Stop');
    this.recordButton = button('Record');
    this.loadFromStorageButton = button('Load from storage');
    this.playStepButton = button('Play step');
    this.header = div('dom-hud-header');
    this.dropDownButton = button('DOMReplay', 'dom-hud-dropDown');

    this.addEventListenerToElement(this.playStepButton, 'click', this.getPlayStepButtonEvent());
    this.addEventListenerToElement(this.startButton, 'click', this.getStartButtonEvent());
    this.addEventListenerToElement(this.loadFromStorageButton, 'click', this.getLoadFromStorageButtonEvent());
    this.addEventListenerToElement(this.recordButton, 'click', this.getRecordButtonEvent());
    this.addEventListenerToElement(this.stopButton, 'click', this.getStopButtonEvent());
    this.addEventListenerToElement(this.dropDownButton, 'click', this.getDropDownButtonEvent());
    this._initializeIndicators();
    this._initializeEventListeners();
  }

  _initializeIndicators() {
  	this.replayStateIndicator = null;
  	this.recordStateIndicator = null;
  	if (this.config.showRecordIndicator && state.stateIsRecord()) {
  		this._renderRecordStateIndicator();
  	}
  	else if (this.config.showReplayIndicator && state.stateIsReplay()) {
  		this._renderReplayStateIndicator();
  	}
  }

  _initializeEventListeners() {
  	dispatcher.addStateChangeEventListener((event) => {
  		if (state.stateIsRecord() && this.config.showRecordIndicator) {
  			this._renderRecordStateIndicator();
  		} else if (state.stateIsReplay() && this.config.showReplayIndicator) {
  			this._renderReplayStateIndicator();
  		} else {
  			this._removeRecordStateIndicator();
  			this._removeReplayStateIndicator();
  		}
  	});
  }

  getPlayStepButtonEvent() {
  	const domreplay = this.domreplay;
  	return () => {
  		domreplay.playStep();
  	}
  }

  getLoadFromStorageButtonEvent() {
  	const domreplay = this.domreplay;
  	return () => {
  		domreplay.loadEventsFromLocalStorage();
  	}
  }

  getStartButtonEvent() {
  	const domreplay = this.domreplay;
  	return () => {
  		domreplay.play();
  	}
  }

  getRecordButtonEvent() {
    const domreplay = this.domreplay;
    return () => {
      domreplay.startRecord();
    };
  }

  getStopButtonEvent() {
    const domreplay = this.domreplay
    return () => {
      domreplay.stopRecord();
    }
  }

  getDropDownButtonEvent() {
    const header = this.header;
    const button = this.dropDownButton;

    return () => {
      if (header.className.indexOf('slide-down') != -1) {
        header.className = header.className.substring(0, header.className.indexOf(' slide-down'));
        header.className += ' slide-up';
      } else {
        if (header.className.indexOf('slide-up') != -1) {
          header.className = header.className.substring(0, header.className.indexOf(' slide-up'));
        }
        header.className += ' slide-down';
      }

      if (button.className.indexOf('slide-down') != -1) {
        button.className = button.className.substring(0, button.className.indexOf(' slide-down'));
        button.className += ' slide-up';
      } else {
        if (button.className.indexOf('slide-up') != -1) {
          button.className = button.className.substring(0, button.className.indexOf(' slide-up'));
        }
        button.className += ' slide-down';
      }
    };
  }

  addEventListenerToElement(element, type, event) {
    element.addEventListener(type, event);
  }

  _renderReplayStateIndicator() {
  	this.replayStateIndicator = div('dom-state-indicator replay');
  	const text = document.createElement('h1');
  	text.innerHTML = "REPLAY";
  	this.replayStateIndicator.appendChild(text);
  	document.getElementsByTagName('body')[0].appendChild(this.replayStateIndicator);
  }

  _removeReplayStateIndicator() {
  	if (this.replayStateIndicator) {
  		this.replayStateIndicator.parentNode.removeChild(this.replayStateIndicator);
  	}
  }

  _renderRecordStateIndicator() {
  	this.recordStateIndicator = div('dom-state-indicator record');
  	const text = document.createElement('h1');
  	text.innerHTML = 'REC';
  	this.recordStateIndicator.appendChild(text);
  	document.getElementsByTagName('body')[0].appendChild(this.recordStateIndicator);
  }

  _removeRecordStateIndicator() {
  	if (this.recordStateIndicator) {
  		this.recordStateIndicator.parentNode.removeChild(this.recordStateIndicator);
  	}
  }

  render() {
    this.header.appendChild(this.startButton);
    this.header.appendChild(this.stopButton);
    this.header.appendChild(this.recordButton);
    this.header.appendChild(this.loadFromStorageButton);
    this.header.appendChild(this.playStepButton);
    let body = document.getElementsByTagName('body')[0];
    body.prepend(this.header);
    body.prepend(this.dropDownButton);
  }

}

