/**
 * Useful when we want to react on state change events in the ui.
 */

import Logger from './logger';

const DOMREPLAY_STATE_CHANGE_EVENT = 'DOMREPLAY_STATE_CHANGE_EVENT'

/**
 * Adds a event listener to the state change event.
 * @param  {Func} func - the function to be called when event has been dispached.
 */
export const addStateChangeEventListener = (func) => {
	Logger.debug('adding add state event listener');
	window.addEventListener(DOMREPLAY_STATE_CHANGE_EVENT, func);
}

/**
 * State change event dispatcher.
 * @param  {String} state - the current state.
 */
export const dispatchStateChangeEvent = (state) => {
	Logger.debug('dispatching state change event');
	const event = new CustomEvent(DOMREPLAY_STATE_CHANGE_EVENT, {
		detail: {
			state,
			message: `State has changed to ${state}`
		}
	});
	window.dispatchEvent(event);
}

import Logger from './logger';

// HTMLElements using this classname will be ignored
export const domreplayIgnoreClassName = 'dom-replay-ignore';

/**
 * Generator function that creates an iterator of
 * elements by tag name
 * @param {String} tagname     - HTML tag name
 * @yield {Element} Element
 */
function* elementByTagNameIterator(tagname) {
	const elements = document.getElementsByTagName(tagname);
	yield* Array.from(elements).map(element => element);
}

/**
 * Initializes events on elements in the dom.
 * @param  {Array} events - list of events
 */
const initializeEvents = (events) => {
	Logger.debug('Initializing existing events.');
	events.forEach(event => {
		event.tagnames.forEach(tagname => {
			for (let element of elementByTagNameIterator(tagname)) {
				if (!element.className.includes(domreplayIgnoreClassName)) {
					element.addEventListener(event.type, () => event.handler(element), false);
				}
			}
		});
	});
}

/**
 * Flattens the node tree and yields only,
 * HTML elements with tagnames that should,
 * be tracked.
 * @param {[type]} mutation      		- mutation object from the mutation observer.
 * @param {Array[String]} tagfilter     - tagnames that should be tracked
 * @yield {HTMLElement} yields trackable HTMLElements.
 */
function* getFlatElementIterator(mutation, tagfilter) {
	const recursiveFlat = function* (node) {
		if (node instanceof HTMLElement &&
					tagfilter.includes(node.tagName.toLowerCase()) &&
					!node.className.includes(domreplayIgnoreClassName)
				) {
			yield node;
		} else if (node.childNodes.length > 0) {
			for (let n of Array.from(node.childNodes)) {
				yield* recursiveFlat(n, tagfilter);
			}
		}
	}

	for (let n of Array.from(mutation.addedNodes)) {
		yield* recursiveFlat(n);
	}
}

/**
 * To ensure that the only top level element is captured in tagnames,
 * we need a filter. for example when a button contains an 'a' tag,
 * only the button is captured.
 * @param  {[type]} events [description]
 * @return {[type]}        [description]
 */
const flatTagnames = (events) => {
	let tagnames = []
	events.forEach(event => {
		tagnames.push(...event.tagnames);
	});
	return tagnames;
}

/**
 * Initializes mutation observer,
 * when ever a new element has been added, it will automaticly
 * add event listener to the element.
 * @param  {Array} events - list of events
 */
const initializeMutationObserver = (events) => {
	Logger.debug('Initializing mutation observer.');
	const tagfilter = flatTagnames(events);
	const analyzeElement = (element) => {
		events.forEach(event => {
			if (event.tagnames.includes(element.tagName.toLowerCase())) {
				Logger.debug(`mutationobserver is adding a ${event.type}-listener to element ${element.id}`);
				element.addEventListener(event.type, () => event.handler(element), false);
			}
		});
	}

	const observer = new MutationObserver(mutations => {
		mutations.forEach(mutation => {
			for (let element of getFlatElementIterator(mutation, tagfilter)) {
				analyzeElement(element);
			}
		});
	});

	const config = {
      childList: true,
      subtree: true
    };

    observer.observe(document.body, config);
}

/**
 * Initializes events on existing elements,
 * and initializes the mutation observer when
 * document is ready.
 * @param  {Array} events - list
 */
export const domloader = (events) => {
	return new Promise((resolve, reject) => {
		const time = setInterval(() => {
			if (document.readyState !== 'complete') {
				Logger.debug('Ready-state check failed!');
				return;
			}
			clearInterval(time);
			initializeEvents(events);
			initializeMutationObserver(events);
			Logger.debug('Done initializing events');
			resolve();
		}, 100);
	});
}

import Util from './utils';
import Storage from './storage';
import { handleClickEvent, handleChangeEvent, handleInputEvent } from './handlers';
import Replay from './replay';
import ServerStorage from './serverstorage';
import { domloader } from './dom_loader';
import Logger from './logger';
import { STATE_ERROR } from './error';
import {
	setStateReady,
	setStateRecord,
	setStateReplay,
	stateIsReplay,
	stateIsRecord,
	stateIsReady
} from './state';


export default class DomReplay {
	/**
	 * The core DomReplay class
	 * @param  {Boolean}  config.debugmode - if true, debug messages wil be logged.
	 * @return {DomReplay} an instance of DomReplay
	 */
  constructor(config = {}) {
  	this.config = config;
    if (config.debugmode) {
    	Logger.logging = true;
    }

    this.util = new Util(config.debugmode);
    this.storage = new Storage(this);
    this.replay = new Replay(this);

    if (!config.events) {
      this.config.events = [{
          type: 'click',
          tagnames: ['a', 'button'],
          handler: handleClickEvent,
        }, {
          type: 'input',
          tagnames: ['input', 'select', 'textarea'],
          handler: handleInputEvent
        }
      ];
    }

    if (this.config.server) {
      this.serverstorage = new ServerStorage(this);
    }

    domloader(this.config.events)
			.then(() => setStateReady())
			.catch(err => {
				if (err.type === STATE_ERROR) {
					return;
				}
				throw err;
			});
  }

  startRecord() {
  	setStateRecord()
  		.catch(err => {
  			if (err === STATE_ERROR) {
  				Logger.warning(err.message);
  			} else {
  				throw err;
  			}
  		});
  }

  stopRecord() {
  	setStateReady(true)
  		.catch(err => {
  			if (err === STATE_ERROR) {
  				Logger.warning(err.message);
  			} else {
  				throw err;
  			}
  		});
  }

  getPlaybackObject() {
    return this.replay;
  }

  pushToServer() {
    if (!this.config.server) {
      this.util.debug('Cannot push to server without server configuration!');
      return;
    }
    this.serverstorage.pushToServer();
  }

  loadFromServer() {
    if (!this.config.server) {
      this.util.debug('Cannot load to server without server configuration!');
      return;
    }
    this.serverstorage.loadFromServer();
  }

  resetStorage() {
    this.storage.reset();
  }
}

/**
 * Constants and error objects which is thrown
 */

export const STATE_ERROR = 'STATE_ERROR';

export const createStateError = (message) => {
	return {
		type: STATE_ERROR,
		message
	};
}

